INSERT INTO world.breeds (Id, ShortName, LongName, Description, GameplayDescriptionId, ShortNameId, LongNameId, DescriptionId, MaleLookString, FemaleLookString, CreatureBonesId, TombBonesId, MaleGhostBonesId, FemaleGhostBonesId, MaleArtwork, FemaleArtwork, StatsPointsForStrengthCSV, StatsPointsForIntelligenceCSV, StatsPointsForChanceCSV, StatsPointsForAgilityCSV, MaleColorsCSV, StatsPointsForVitalityCSV, StatsPointsForWisdomCSV, BreedSpellsIdCSV, FemaleColorsCSV, StartMap, StartCell, StartDirection, StartActionPoints, StartMovementPoints, StartHealthPoint, StartProspection, StartStatsPoints, StartSpellsPoints, StartStrength, StartVitality, StartWisdom, StartIntelligence, StartChance, StartAgility, StartLevel, StartKamas) VALUES (1, 'Feca', 'El Escudo de Feca', 'Los fecas son leales protectores que siempre están en guardia. Los grupos de aventureros se los rifan por sus armaduras elementales, además de por su capacidad de encajar golpes duros. Son maestros del arte de los símbolos mágicos: unos defensores fuera de serie que sacan sus glifos cuando hay guerra.', 598583, 685, 116103, 305968, '{1|10||135}', '{1|11||125}', 15, 0, 0, 0, 198, 199, '0,1|100,2|200,3|300,4', '0,1|100,2|200,3|300,4', '0,1|100,2|200,3|300,4', '0,1|100,2|200,3|300,4', '16765564,7098157,15972912,5468254,7098157', '0,1', '0,3', '3,6,2,1,19,4,17,18,9,20,8,11,13,5,12,14,15,422,16,10,7', '16103775,6833184,13661190,13661190,6963462', 152043777, 287, 3, 6, 3, 55, 100, 0, 0, 0, 0, 0, 0, 0, 0, 1, 100);
INSERT INTO world.breeds (Id, ShortName, LongName, Description, GameplayDescriptionId, ShortNameId, LongNameId, DescriptionId, MaleLookString, FemaleLookString, CreatureBonesId, TombBonesId, MaleGhostBonesId, FemaleGhostBonesId, MaleArtwork, FemaleArtwork, StatsPointsForStrengthCSV, StatsPointsForIntelligenceCSV, StatsPointsForChanceCSV, StatsPointsForAgilityCSV, MaleColorsCSV, StatsPointsForVitalityCSV, StatsPointsForWisdomCSV, BreedSpellsIdCSV, FemaleColorsCSV, StartMap, StartCell, StartDirection, StartActionPoints, StartMovementPoints, StartHealthPoint, StartProspection, StartStatsPoints, StartSpellsPoints, StartStrength, StartVitality, StartWisdom, StartIntelligence, StartChance, StartAgility, StartLevel, StartKamas) VALUES (2, 'Osamodas', 'El Látigo de Osamodas', '¡Los osamodas son domadores natos! Tienen el poder de invocar criaturas y resultan ser excelentes adiestradores. Corre el rumor de que utilizan la piel de sus enemigos para vestirse. Si quieres saber si es cierto, puedes ir tú mismo a preguntarles...  Si estás de su lado, el osamodas te mimará. En caso contrario, puede que termines transformado en algún que otro complemento de moda.', 598597, 697, 116101, 305966, '{1|20||130}', '{1|21||125}', 16, 0, 0, 0, 200, 202, '0,1|100,2|200,3|300,4', '0,1|100,2|200,3|300,4', '0,1|100,2|200,3|300,4', '0,1|100,2|200,3|300,4', '15973470,16777166,4836573,2827027,16761904', '0,1', '0,3', '21,22,34,26,28,35,37,38,30,33,40,39,24,9664,36,9666,9667,420,31,27,29', '16759376,16777164,2726594,2827027,2726594', 152043777, 287, 3, 6, 3, 55, 100, 0, 0, 0, 0, 0, 0, 0, 0, 1, 100);
INSERT INTO world.breeds (Id, ShortName, LongName, Description, GameplayDescriptionId, ShortNameId, LongNameId, DescriptionId, MaleLookString, FemaleLookString, CreatureBonesId, TombBonesId, MaleGhostBonesId, FemaleGhostBonesId, MaleArtwork, FemaleArtwork, StatsPointsForStrengthCSV, StatsPointsForIntelligenceCSV, StatsPointsForChanceCSV, StatsPointsForAgilityCSV, MaleColorsCSV, StatsPointsForVitalityCSV, StatsPointsForWisdomCSV, BreedSpellsIdCSV, FemaleColorsCSV, StartMap, StartCell, StartDirection, StartActionPoints, StartMovementPoints, StartHealthPoint, StartProspection, StartStatsPoints, StartSpellsPoints, StartStrength, StartVitality, StartWisdom, StartIntelligence, StartChance, StartAgility, StartLevel, StartKamas) VALUES (3, 'Anutrof', 'Los Dedos de Anutrof', 'Los anutrofs son impenitentes cazadores de tesoros sedientos de kamas que, a pesar de su edad avanzada, corren como dragopavos al ver un cofre bien repleto. Son expertos en el arte de ralentizar a sus enemigos: así pueden atosigarlos antes de dejarles fuera de juego ¡a palazo limpio!', 598579, 681, 116105, 305969, '{1|30||120}', '{1|31||95}', 17, 0, 0, 0, 203, 204, '0,1|100,2|200,3|300,4', '0,1|100,2|200,3|300,4', '0,1|100,2|200,3|300,4', '0,1|100,2|200,3|300,4', '15973470,16777166,4404257,16767286,10904329', '0,1', '0,3', '51,41,43,49,55,47,48,45,53,57,52,44,50,54,42,56,58,425,59,46,60', '15384674,15982756,4404257,16777164,11373881', 152043777, 287, 3, 6, 3, 55, 100, 0, 0, 0, 0, 0, 0, 0, 0, 1, 100);
INSERT INTO world.breeds (Id, ShortName, LongName, Description, GameplayDescriptionId, ShortNameId, LongNameId, DescriptionId, MaleLookString, FemaleLookString, CreatureBonesId, TombBonesId, MaleGhostBonesId, FemaleGhostBonesId, MaleArtwork, FemaleArtwork, StatsPointsForStrengthCSV, StatsPointsForIntelligenceCSV, StatsPointsForChanceCSV, StatsPointsForAgilityCSV, MaleColorsCSV, StatsPointsForVitalityCSV, StatsPointsForWisdomCSV, BreedSpellsIdCSV, FemaleColorsCSV, StartMap, StartCell, StartDirection, StartActionPoints, StartMovementPoints, StartHealthPoint, StartProspection, StartStatsPoints, StartSpellsPoints, StartStrength, StartVitality, StartWisdom, StartIntelligence, StartChance, StartAgility, StartLevel, StartKamas) VALUES (4, 'Sram', 'La Sombra de Sram', 'Los srams son asesinos a los que les encantan las bolsas y más aún cuando están repletitas de kamas. La vida de un sram se podría resumir a las siguientes acciones: rasgar túnicas, palpar las joyas valiosas antes de poner una trampa o asestar un golpe mortal, y la no menos útil prestidigitación... ¡Ahora la ves, ahora no la ves... porque la joya yo te robé!', 598617, 701, 116106, 305965, '{1|40||140}', '{1|41||155}', 18, 0, 0, 0, 205, 206, '0,1|100,2|200,3|300,4', '0,1|100,2|200,3|300,4', '0,1|100,2|200,3|300,4', '0,1|100,2|200,3|300,4', '15919294,3101814,3101814,3101814,8748628', '0,1', '0,3', '61,65,72,66,68,63,74,75,79,78,71,62,69,77,73,67,70,416,64,76,80', '15384674,16777198,4157854,4157854,14872505', 152043777, 287, 3, 6, 3, 55, 100, 0, 0, 0, 0, 0, 0, 0, 0, 1, 100);
INSERT INTO world.breeds (Id, ShortName, LongName, Description, GameplayDescriptionId, ShortNameId, LongNameId, DescriptionId, MaleLookString, FemaleLookString, CreatureBonesId, TombBonesId, MaleGhostBonesId, FemaleGhostBonesId, MaleArtwork, FemaleArtwork, StatsPointsForStrengthCSV, StatsPointsForIntelligenceCSV, StatsPointsForChanceCSV, StatsPointsForAgilityCSV, MaleColorsCSV, StatsPointsForVitalityCSV, StatsPointsForWisdomCSV, BreedSpellsIdCSV, FemaleColorsCSV, StartMap, StartCell, StartDirection, StartActionPoints, StartMovementPoints, StartHealthPoint, StartProspection, StartStatsPoints, StartSpellsPoints, StartStrength, StartVitality, StartWisdom, StartIntelligence, StartChance, StartAgility, StartLevel, StartKamas) VALUES (5, 'Xelor', 'El Reloj de Arena de Xelor', 'Los xelors son magos que dominan el tiempo y todos los mecanismos que dan la hora: carrillones, relojes de pared y péndulos les obedecen a la perfección. Juegan con el tiempo para ralentizar a un enemigo o teletransportarse al lugar que les apetezca.', 598627, 705, 706, 305960, '{1|50||110}', '{1|51||110}', 22, 0, 0, 0, 207, 208, '0,1|100,2|200,3|300,4', '0,1|100,2|200,3|300,4', '0,1|100,2|200,3|300,4', '0,1|100,2|200,3|300,4', '7957320,6178082,2775654,14523954,4336658', '0,1', '0,3', '83,81,88,93,82,91,9894,84,100,98,85,96,424,87,89,86,94,90,95,92,99', '7957320,16777166,2512998,13945509,2512998', 152043777, 287, 3, 6, 3, 55, 100, 0, 0, 0, 0, 0, 0, 0, 0, 1, 100);
INSERT INTO world.breeds (Id, ShortName, LongName, Description, GameplayDescriptionId, ShortNameId, LongNameId, DescriptionId, MaleLookString, FemaleLookString, CreatureBonesId, TombBonesId, MaleGhostBonesId, FemaleGhostBonesId, MaleArtwork, FemaleArtwork, StatsPointsForStrengthCSV, StatsPointsForIntelligenceCSV, StatsPointsForChanceCSV, StatsPointsForAgilityCSV, MaleColorsCSV, StatsPointsForVitalityCSV, StatsPointsForWisdomCSV, BreedSpellsIdCSV, FemaleColorsCSV, StartMap, StartCell, StartDirection, StartActionPoints, StartMovementPoints, StartHealthPoint, StartProspection, StartStatsPoints, StartSpellsPoints, StartStrength, StartVitality, StartWisdom, StartIntelligence, StartChance, StartAgility, StartLevel, StartKamas) VALUES (6, 'Zurcarák', 'La Moneda de Zurcarák', 'Los guerreros zurcaráks son jugadores empedernidos capaces incluso de arriesgar una de sus siete vidas por una simple apuesta. Un buen zurcarák no lo dudará un segundo, si tiene que jugarse el pellejo para llevarse el premio gordo, lanzará los dados al aire para llevárselo todo... aunque, a veces, la suerte es traicionera y ¡¡puede que lo pierda todo!!', 598087, 677, 116109, 305963, '{1|60||150}', '{1|61||150}', 34, 0, 0, 0, 209, 210, '0,1|100,2|200,3|300,4', '0,1|100,2|200,3|300,4', '0,1|100,2|200,3|300,4', '0,1|100,2|200,3|300,4', '16380634,13726012,16735778,2786186,16761904', '0,1', '0,3', '102,103,105,109,113,107,117,119,101,111,116,106,108,104,115,118,110,412,112,114,120', '16380634,14378769,14568735,13201181,2057046', 152043777, 287, 3, 6, 3, 55, 100, 0, 0, 0, 0, 0, 0, 0, 0, 1, 100);
INSERT INTO world.breeds (Id, ShortName, LongName, Description, GameplayDescriptionId, ShortNameId, LongNameId, DescriptionId, MaleLookString, FemaleLookString, CreatureBonesId, TombBonesId, MaleGhostBonesId, FemaleGhostBonesId, MaleArtwork, FemaleArtwork, StatsPointsForStrengthCSV, StatsPointsForIntelligenceCSV, StatsPointsForChanceCSV, StatsPointsForAgilityCSV, MaleColorsCSV, StatsPointsForVitalityCSV, StatsPointsForWisdomCSV, BreedSpellsIdCSV, FemaleColorsCSV, StartMap, StartCell, StartDirection, StartActionPoints, StartMovementPoints, StartHealthPoint, StartProspection, StartStatsPoints, StartSpellsPoints, StartStrength, StartVitality, StartWisdom, StartIntelligence, StartChance, StartAgility, StartLevel, StartKamas) VALUES (7, 'Aniripsa', 'Las Manos de Aniripsa', 'Los aniripsas son curanderos que curan con una simple palabra. Las utilizan sobre todo para aliviar el sufrimiento de sus aliados pero, a veces, también para herir a sus enemigos. Entre ellos, los hay que se han convertido en cazadores de palabras, en expertos del verbo o en exploradores de los idiomas olvidados.', 598094, 673, 582016, 305962, '{1|70||110}', '{1|71||115}', 36, 0, 0, 0, 211, 212, '0,1|100,2|200,3|300,4', '0,1|100,2|200,3|300,4', '0,1|100,2|200,3|300,4', '0,1|100,2|200,3|300,4', '16765564,7360545,14453532,6048816,4734007', '0,1', '0,3', '129,124,122,125,128,126,427,133,130,134,127,123,136,135,132,121,138,131,137,139,140', '14785337,16707772,16707772,11941144,15388778', 152043777, 287, 3, 6, 3, 55, 100, 0, 0, 0, 0, 0, 0, 0, 0, 1, 100);
INSERT INTO world.breeds (Id, ShortName, LongName, Description, GameplayDescriptionId, ShortNameId, LongNameId, DescriptionId, MaleLookString, FemaleLookString, CreatureBonesId, TombBonesId, MaleGhostBonesId, FemaleGhostBonesId, MaleArtwork, FemaleArtwork, StatsPointsForStrengthCSV, StatsPointsForIntelligenceCSV, StatsPointsForChanceCSV, StatsPointsForAgilityCSV, MaleColorsCSV, StatsPointsForVitalityCSV, StatsPointsForWisdomCSV, BreedSpellsIdCSV, FemaleColorsCSV, StartMap, StartCell, StartDirection, StartActionPoints, StartMovementPoints, StartHealthPoint, StartProspection, StartStatsPoints, StartSpellsPoints, StartStrength, StartVitality, StartWisdom, StartIntelligence, StartChance, StartAgility, StartLevel, StartKamas) VALUES (8, 'Yopuka', 'El Corazón de Yopuka', '¡Los yopukas son intrépidos y temerarios guerreros! Lo cierto es que saben hacer hablar a las armas como nadie. Y lo de que una buena pelea por la mañana temprano les recarga las pilas para todo el día, es una verdad como un puño. Su impetuoso temperamento hace que sean unos maravillosos paladines, capaces de lo mejor... y de lo peor.', 598592, 693, 694, 305967, '{1|80||140}', '{1|81||140}', 38, 0, 0, 0, 213, 214, '0,1|100,2|200,3|300,4', '0,1|100,2|200,3|300,4', '0,1|100,2|200,3|300,4', '0,1|100,2|200,3|300,4', '16765564,16355883,16777185,2910064,14561799', '0,1', '0,3', '141,143,142,410,145,154,147,151,158,150,148,144,152,153,149,156,157,155,160,146,159', '16103775,15430419,16777185,14432786,14432786', 152043777, 287, 3, 6, 3, 55, 100, 0, 0, 0, 0, 0, 0, 0, 0, 1, 100);
INSERT INTO world.breeds (Id, ShortName, LongName, Description, GameplayDescriptionId, ShortNameId, LongNameId, DescriptionId, MaleLookString, FemaleLookString, CreatureBonesId, TombBonesId, MaleGhostBonesId, FemaleGhostBonesId, MaleArtwork, FemaleArtwork, StatsPointsForStrengthCSV, StatsPointsForIntelligenceCSV, StatsPointsForChanceCSV, StatsPointsForAgilityCSV, MaleColorsCSV, StatsPointsForVitalityCSV, StatsPointsForWisdomCSV, BreedSpellsIdCSV, FemaleColorsCSV, StartMap, StartCell, StartDirection, StartActionPoints, StartMovementPoints, StartHealthPoint, StartProspection, StartStatsPoints, StartSpellsPoints, StartStrength, StartVitality, StartWisdom, StartIntelligence, StartChance, StartAgility, StartLevel, StartKamas) VALUES (9, 'Ocra', 'El Alcance de Ocra', 'Precisión, agilidad y destreza son las palabras clave para describir a los ocras. Estos arqueros son unos valiosos aliados contra los adeptos al cuerpo a cuerpo. Saben guardar las distancias para dominar el campo de batalla lanzando flechas aceradas al enemigo. ¡Vamos, que saben cómo hacer daño!', 598088, 689, 116108, 305970, '{1|90||140}', '{1|91||135}', 40, 0, 0, 0, 215, 216, '0,1|100,2|200,3|300,4', '0,1|100,2|200,3|300,4', '0,1|100,2|200,3|300,4', '0,1|100,2|200,3|300,4', '16762983,14774542,5792768,5845776,16422159', '0,1', '0,3', '161,169,418,163,165,172,167,168,162,170,171,166,173,174,176,175,178,164,177,179,180', '16761703,14775312,8091904,14053895,6963462', 152043777, 287, 3, 6, 3, 55, 100, 0, 0, 0, 0, 0, 0, 0, 0, 1, 100);
INSERT INTO world.breeds (Id, ShortName, LongName, Description, GameplayDescriptionId, ShortNameId, LongNameId, DescriptionId, MaleLookString, FemaleLookString, CreatureBonesId, TombBonesId, MaleGhostBonesId, FemaleGhostBonesId, MaleArtwork, FemaleArtwork, StatsPointsForStrengthCSV, StatsPointsForIntelligenceCSV, StatsPointsForChanceCSV, StatsPointsForAgilityCSV, MaleColorsCSV, StatsPointsForVitalityCSV, StatsPointsForWisdomCSV, BreedSpellsIdCSV, FemaleColorsCSV, StartMap, StartCell, StartDirection, StartActionPoints, StartMovementPoints, StartHealthPoint, StartProspection, StartStatsPoints, StartSpellsPoints, StartStrength, StartVitality, StartWisdom, StartIntelligence, StartChance, StartAgility, StartLevel, StartKamas) VALUES (10, 'Sadida', 'El Zapato de Sadida', 'Los sadidas son invocadores y envenenadores sin igual: vamos que saben cómo amargarles la vida a sus enemigos. Cualquier sadida digno de su condición es capaz de confeccionar muñecas de curación y de guerra y de invocar zarzas para atacar a sus enemigos.', 598613, 661, 662, 305961, '{1|100||150}', '{1|101||145}', 12, 0, 0, 0, 217, 218, '0,1|100,2|200,3|300,4', '0,1|100,2|200,3|300,4', '0,1|100,2|200,3|300,4', '0,1|100,2|200,3|300,4', '8537887,8164099,14402669,14402669,11960892', '0,1', '0,3', '9750,183,186,200,195,9753,192,197,9756,181,199,191,198,196,9763,194,185,426,184,188,9769', '8537887,8557826,16114596,16114596,15128478', 152043777, 287, 3, 6, 3, 55, 100, 0, 0, 0, 0, 0, 0, 0, 0, 1, 100);
INSERT INTO world.breeds (Id, ShortName, LongName, Description, GameplayDescriptionId, ShortNameId, LongNameId, DescriptionId, MaleLookString, FemaleLookString, CreatureBonesId, TombBonesId, MaleGhostBonesId, FemaleGhostBonesId, MaleArtwork, FemaleArtwork, StatsPointsForStrengthCSV, StatsPointsForIntelligenceCSV, StatsPointsForChanceCSV, StatsPointsForAgilityCSV, MaleColorsCSV, StatsPointsForVitalityCSV, StatsPointsForWisdomCSV, BreedSpellsIdCSV, FemaleColorsCSV, StartMap, StartCell, StartDirection, StartActionPoints, StartMovementPoints, StartHealthPoint, StartProspection, StartStatsPoints, StartSpellsPoints, StartStrength, StartVitality, StartWisdom, StartIntelligence, StartChance, StartAgility, StartLevel, StartKamas) VALUES (11, 'Sacrógrito', 'La Sangre de Sacrógrito', '¡Los sacrógritos son berserkers cuya fuerza aumenta cuando reciben golpes! Cualquier grupo debería tener en sus filas a, por lo menos, uno de estos guerreros ya que no temen las heridas y muy a menudo están en primera línea de batalla, dispuestos a derramar sangre. El sacrógrito es realmente el compañero ideal para pasar largas noches luchando.', 598609, 665, 666, 305964, '{1|110||145}', '{1|111||140}', 13, 0, 0, 0, 219, 220, '0,1|100,2|200,3|300,4', '0,1|100,2|200,3|300,4', '0,1|100,2|200,3|300,4', '0,1|100,2|200,3|300,4', '16765564,16753434,9809704,5664802,16771138', '0,1', '0,3', '9967,9968,9948,9960,9963,9978,9966,9970,9969,9956,9959,9972,9958,9977,9971,9975,9957,9961,9973,9964,9965', '16565105,16753434,5992482,6003530,16769340', 152043777, 287, 3, 6, 3, 55, 100, 0, 0, 0, 0, 0, 0, 0, 0, 1, 100);
