create table emotes
(
    id               int          not null
        primary key,
    Name             varchar(255) null,
    nameId           int          not null,
    shortcutId       int          null,
    `order`          varchar(255) null,
    defaultAnim      varchar(255) null,
    persistancy      tinyint      not null,
    eight_directions tinyint      not null,
    aura             tinyint      not null,
    anims            varchar(255) null,
    cooldown         int          not null,
    duration         int          not null,
    weight           varchar(255) null,
    spellLevelId     varchar(255) null
)
    engine = MyISAM
    charset = latin1;

