create table idols_templates
(
    Id                      int        not null
        primary key,
    Description             mediumtext not null,
    IdolItemId              int        not null,
    IdolSpellId             int        null,
    IdolSpellLevel          tinyint    not null,
    CategoryId              int        not null,
    GroupOnly               tinyint    not null,
    Score                   int        not null,
    ExperienceBonus         int        not null,
    DropBonus               int        not null,
    SynergyIdolsIdsCSV      mediumtext null,
    SynergyIdolsCoefCSV     mediumtext null,
    IncompatibleMonstersCSV mediumtext null,
    TargetString            mediumtext null
)
    engine = MyISAM
    charset = utf8;

