create table interactives_skills_templates
(
    Id                     int          not null
        primary key,
    NameId                 int unsigned not null,
    Name                   varchar(255) null,
    ParentJobId            int          not null,
    IsForgemagus           tinyint      not null,
    ModifiableItemTypesCSV mediumtext   not null,
    GatheredRessourceItem  int          not null,
    CraftableItemIdsCSV    mediumtext   not null,
    InteractiveId          int          not null,
    UseAnimation           mediumtext   not null,
    `Cursor`               int          not null,
    ElementActionId        int          not null,
    AvailableInHouse       tinyint      not null,
    LevelMin               int unsigned not null,
    ClientDisplay          tinyint      not null
)
    engine = MyISAM
    charset = utf8;

