INSERT INTO world.langs_ui (Id, Name, French, English, German, Spanish, Italian, Japanish, Dutsh, Portugese, Russish) VALUES (3919, 'ui.breachReward.defeatInsurance', 'Chaque défaite dans le Puits des Songes Infinis se solde par une perte de points de rêve. Pour le prix d''une défaite, l''assurance permet de recommencer le niveau de profondeur courant autant de fois que nécessaire sans perte de points supplémentaire.', 'Each defeat in the Well of Infinite Dreams results in losing dream points. For the price of a defeat, insurance allows you to restart the current depth level as many times as necessary without losing additional points.', null, 'Cada derrota en el pozo de los Sueños Infinitos se salda con una pérdida de puntos de sueño. Por el precio de una derrota, el seguro permite repetir el nivel de profundidad corriente tantas veces como sea necesario sin pérdida de puntos adicional.', null, null, null, 'Cada derrota no Poço dos Sonhos Infinitos acarreta perda de pontos de sonho. Pelo mesmo preço de uma derrota, o seguro permite que você recomece o nível de profundidade atual quantas vezes for preciso sem perder mais pontos.', null);
INSERT INTO world.langs_ui (Id, Name, French, English, German, Spanish, Italian, Japanish, Dutsh, Portugese, Russish) VALUES (3920, 'ui.breachReward.positiveModifier', 'Remplace les rêves, paradoxes et cauchemars des salles de combat actuelles par l''effet suivant.', 'Replaces dreams, paradoxes and nightmares of the current combat rooms with the following effect.', null, 'Sustituye los sueños, paradojas y pesadillas de las salas de combate actuales por el efecto siguiente.', null, null, null, 'Troca os sonhos, paradoxos e pesadelos das salas de combate atuais pelo efeito seguinte.', null);
INSERT INTO world.langs_ui (Id, Name, French, English, German, Spanish, Italian, Japanish, Dutsh, Portugese, Russish) VALUES (3921, 'ui.breachReward.reroll', 'Renvoie les gardiens de donjon actuels dans le plan astral et fait apparaître de nouveaux gardiens à la place.', 'Returns the current dungeon keepers to the astral plane and makes new keepers appear in their place.', null, 'Devuelve los guardianes de mazmorra actuales al plano astral y hace que aparezcan nuevos guardianes en su lugar.', null, null, null, 'Reenvia os guardiões de calabouço atuais ao plano astral e faz aparecer novos guardiões no lugar deles.', null);
INSERT INTO world.langs_ui (Id, Name, French, English, German, Spanish, Italian, Japanish, Dutsh, Portugese, Russish) VALUES (3922, 'ui.breachReward.revitalisation', 'À chaque combat, une partie des points de vie perdus par l''équipe disparaît dans l''infinité du puits sous forme d''érosion. Restaure l''intégralité de cette érosion.', 'Each fight, some of the health points lost by the team disappear into the infinity of the well as erosion. Restores all of this erosion.', null, 'Con cada combate, una parte de los puntos de vida perdidos por el equipo desaparece en la infinidad del pozo por erosión. Restaura la totalidad de esta erosión.', null, null, null, 'Em todos os combates, parte dos pontos de vida perdidos pela equipe desaparece na infinitude do poço na forma de erosão. Restaura esta erosão integralmente.', null);
