create table mounts_templates
(
    Id               int          not null
        primary key,
    Name             varchar(255) null,
    NameId           int unsigned not null,
    LookAsString     mediumtext   not null,
    ScrollId         int unsigned not null,
    PodsBase         int          not null,
    PodsPerLevel     int          not null,
    EnergyBase       int          not null,
    EnergyPerLevel   int          not null,
    MaturityBase     int          not null,
    FecondationTime  int          not null,
    LearnCoefficient tinyint      null,
    FamilyId         int          not null
)
    engine = MyISAM
    charset = utf8;

